import React from 'react';
import { Chart } from 'react-google-charts';

const GoogleChart = ({ data }) => {
  const chartData = [['Candidate', 'Votes'], ...data.map(item => [item.candidate, item.totalVotes])];

  return (
    <div>
      <Chart
        width={'600px'}
        height={'400px'}
        chartType="Bar"
        loader={<div>Loading Chart...</div>}
        data={chartData}
        options={{
          title: 'Voting Results',
          chartArea: { width: '50%' },
          hAxis: {
            title: 'Total Votes',
            minValue: 0,
          },
          vAxis: {
            title: 'Candidates',
          },
        }}
      />
    </div>
  );
};

export default GoogleChart;
