// src/components/VoterDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [selectedCandidate, setSelectedCandidate] = useState('');

  useEffect(() => {
    const fetchCandidates = async () => {
      const res = await axios.get('/api/candidates');
      setCandidates(res.data);
    };

    fetchCandidates();
  }, []);

  const handleVote = async () => {
    await axios.post('/api/votes', { candidateId: selectedCandidate });
    // Optionally, show a success message or reset selection
    setSelectedCandidate('');
  };

  return (
    <div>
      <h1>Voter Dashboard</h1>
      <h2>Select a Candidate to Vote</h2>
      <ul>
        {candidates.map(candidate => (
          <li key={candidate._id}>
            <label>
              <input 
                type="radio" 
                name="candidate" 
                value={candidate._id} 
                onChange={(e) => setSelectedCandidate(e.target.value)} 
              />
              {candidate.name} - {candidate.facility}
            </label>
          </li>
        ))}
      </ul>
      <button onClick={handleVote} disabled={!selectedCandidate}>
        Vote
      </button>
    </div>
  );
};

export default VoterDashboard;
