// src/components/CandidateDashboard.js
import React, { useState } from 'react';
import axios from 'axios';

const CandidateDashboard = () => {
  const [candidate, setCandidate] = useState({ name: '', facility: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('/api/candidates', candidate);
    setCandidate({ name: '', facility: '' });
  };

  return (
    <div>
      <h1>Candidate Dashboard</h1>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="Name" 
          value={candidate.name} 
          onChange={(e) => setCandidate({ ...candidate, name: e.target.value })} 
          required 
        />
        <input 
          type="text" 
          placeholder="Facility" 
          value={candidate.facility} 
          onChange={(e) => setCandidate({ ...candidate, facility: e.target.value })} 
          required 
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default CandidateDashboard;
