import React, { useState } from 'react';
import api from '../services/api';
import './CandidateDashboard.css';

const CandidateDashboard = () => {
  const [name, setName] = useState('');
  const [facilities, setFacilities] = useState('');

  const submitCandidateDetails = async (e) => {
    e.preventDefault();
    await api.post('/api/candidate', { name, facilities });
    alert('Candidate details submitted successfully!');
  };

  return (
    <div className="candidate-dashboard">
      <h2>Candidate Dashboard</h2>
      <form onSubmit={submitCandidateDetails}>
        <div>
          <label>Name</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div>
          <label>Facilities</label>
          <textarea value={facilities} onChange={(e) => setFacilities(e.target.value)} required />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default CandidateDashboard;
