import React, { useEffect, useState } from 'react';
import api from '../services/api';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [voters, setVoters] = useState([]);
  const [results, setResults] = useState([]);

  useEffect(() => {
    // Fetch candidates, voters, and results
    fetchCandidates();
    fetchVoters();
    fetchResults();
  }, []);

  const fetchCandidates = async () => {
    const res = await api.get('/api/candidate');
    setCandidates(res.data);
  };

  const fetchVoters = async () => {
    const res = await api.get('/api/voter');
    setVoters(res.data);
  };

  const fetchResults = async () => {
    const res = await api.get('/api/results');
    setResults(res.data);
  };

  const deleteCandidate = async (id) => {
    await api.delete(`/api/candidate/${id}`);
    fetchCandidates();  // Refresh list after deletion
  };

  const deleteVoter = async (id) => {
    await api.delete(`/api/voter/${id}`);
    fetchVoters();  // Refresh list after deletion
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>

      <section>
        <h3>Manage Candidates</h3>
        <ul>
          {candidates.map(candidate => (
            <li key={candidate._id}>
              {candidate.name} - {candidate.facilities}
              <button onClick={() => deleteCandidate(candidate._id)}>Delete</button>
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h3>Manage Voters</h3>
        <ul>
          {voters.map(voter => (
            <li key={voter._id}>
              {voter.email}
              <button onClick={() => deleteVoter(voter._id)}>Delete</button>
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h3>Results</h3>
        {results.length > 0 ? (
          <ul>
            {results.map(result => (
              <li key={result._id}>{result.name}: {result.votes} votes</li>
            ))}
          </ul>
        ) : (
          <p>No results available yet</p>
        )}
      </section>
    </div>
  );
};

export default AdminDashboard;
